import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheck } from "@fortawesome/free-solid-svg-icons";
import styles from "./Signup.module.css";
import SignupAgreement from "./SignupAgreement";
import NickName from "./NickName";
import NonMemberResSearch from "./NonMemberResSearch";
import RegisterPhone from "./RegisterPhone";

const Signup1 = (props) => {
  const [email, setEmail] = useState(""); // Ïù¥Î©îÏùº
  const [password, setPassword] = useState(""); // ÎπÑÎ∞ÄÎ≤àÌò∏
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isPasswordMatched, setIsPasswordMatched] = useState(false);
  const [nickNameValue, setnickNameValue] = useState("");
  const [agreementValue, setAgreementValue] = useState("");
  const [count, setCount] = useState(1);

  const getAgreementValue = (agreementValue) => {
    setAgreementValue(agreementValue);
    console.log(agreementValue);
  };

  const getNickName = (nickNameValue) => {
    setnickNameValue(nickNameValue);
    console.log();
  };

  const allData = {
    email: { email },
    password: { password },
    agreementValue: { agreementValue },
    nickNameValue: { nickNameValue },
  };

  const onNext = () => {
    if (email === "" || password === "" || confirmPassword === "")
      setCount(count);
    else {
      setCount(count + 1);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Ïù¥Î©îÏùºÍ≥º ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏ Î°úÏßÅ Íµ¨ÌòÑ
    if (password === confirmPassword) {
      // Ïù¥Î©îÏùºÍ≥º ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÎäî Í≤ΩÏö∞ Í∞ÄÏûÖ ÌîÑÎ°úÏÑ∏Ïä§ ÏßÑÌñâ
      alert("Í∞ÄÏûÖ ÏÑ±Í≥µ!");
      // Ïó¨Í∏∞ÏÑú Í∞ÄÏûÖ ÌîÑÎ°úÏÑ∏Ïä§Î•º ÏßÑÌñâÌïòÎ©¥ Îê©ÎãàÎã§.
    } else {
      // Ïù¥Î©îÏùºÍ≥º ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÎäî Í≤ΩÏö∞ Ï≤òÎ¶¨ Î°úÏßÅ
      alert("ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.");
    }
  };

  //---------- Ïù¥Î©îÏùº ÌòïÏãù Ï≤¥ÌÅ¨ ----------
  const [validateEmail, setValidateEmail] = useState(false);

  const emailEventHandler = (e) => {
    const enteredEmail = e.target.value.replace(/\s/g, "").trim();
    setEmail(enteredEmail);
    const regex1 =
      /^[A-Za-z0-9]([-_.]?[A-Za-z0-9])*@[A-Za-z0-9]([-_.]?[A-Za-z0-9])*\.[A-Za-z]{2,3}$/;
    const isValidEmail = regex1.test(enteredEmail);

    setValidateEmail(isValidEmail);
  };

  // ---------- ÎπÑÎ∞ÄÎ≤àÌò∏ ÌòïÏãù Ï≤¥ÌÅ¨ ----------
  const [validatePwd, setValidatePwd] = useState(false);

  const pwdEventHandler = (e) => {
    setPassword(e.target.value);
    const reg2 = /^(?=.*[a-zA-Z])(?=.*[!@#$%^*+=-])(?=.*[0-9]).{8,15}$/;
    const isValidPwd = reg2.test(e.target.value);

    setValidatePwd(isValidPwd);
  };

  // ---------- ÎπÑÎ∞ÄÎ≤àÌò∏ ÎèôÏùº Ïó¨Î∂Ä Ï≤¥ÌÅ¨ ----------
  const [isPwdMatched, setIsPwdMatched] = useState(false);

  const pwdMatchCheckHandler = (e) => {
    setConfirmPassword(e.target.value);
    if (password !== e.target.value) {
      setIsPwdMatched(false);
    } else {
      setIsPwdMatched(true);
    }
  };

  // ---------- Ïù¥Î©îÏùº Ï§ëÎ≥µ Í≤ÄÏÇ¨ ----------
  const dplChkEmailHandler = async () => {
    try { 
        const response = await fetch("/user/dplChkEmail", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(email),
          });

      if (!result) {
          setNicknameMsg("Ïù¥ÎØ∏ Îì±Î°ùÎêú ÎãâÎÑ§ÏûÑÏûÖÎãàÎã§. Îã§Ïãú ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
          setCheckNickname(false);
     } else {
        setNicknameMsg("ÏÇ¨Ïö© Í∞ÄÎä•Ìïú ÎãâÎÑ§ÏûÑÏûÖÎãàÎã§.üòä");
        setCheckNickname(true);
      }

    } catch (err) {
      console.log(err);
    }
  }

  return (
    <div className={styles["width-wrapper"]}>
      {count === 1 && (
        <form className={styles["width-wrapper-form"]} onSubmit={handleSubmit}>
          <h1 className={styles["signup-heading"]}>Ïù¥Î©îÏùºÎ°ú Í∞ÄÏûÖÌïòÍ∏∞</h1>

          <div className={styles["input-group"]}>
            <label className={styles.label}>
              <h2 className={styles["input-label"]}>Ïù¥Î©îÏùº Ï£ºÏÜå</h2>
            </label>
            <div className={styles["input-field"]}>
              <input
                type="email"
                value={email}
                onChange={emailEventHandler}
                placeholder="abc@gmail.com"
                required
                className={styles["input"]}
              />
              {validateEmail ? (
                <div className={styles["input-field-valEmail"]}>
                  Ïò¨Î∞îÎ•∏ Ïù¥Î©îÏùº ÌòïÏãùÏûÖÎãàÎã§ :)
                </div>
              ) : (
                <div className={styles["input-field-inValEmail"]}>
                  Ïù¥Î©îÏùº ÌòïÏãùÏù¥ Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§ :(
                </div>
              )}
            </div>
          </div>

          <br />
          <div className={styles["input-group"]}>
            <label className={styles.label}>
              <h2 className={styles["input-label"]}>ÎπÑÎ∞ÄÎ≤àÌò∏</h2>
            </label>
            <div className={styles["input-field"]}>
              <input
                type="password"
                value={password}
                onChange={pwdEventHandler}
                placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏ ÏûÖÎ†•"
                required
                className={styles["input"]}
              />
              {validatePwd ? (
                <div className={styles["input-field-valEmail"]}>
                  Ïò¨Î∞îÎ•∏ ÎπÑÎ∞ÄÎ≤àÌò∏ ÌòïÏãùÏûÖÎãàÎã§ :)
                </div>
              ) : (
                <div className={styles["input-field-inValEmail"]}>
                  Ïà´Ïûê+ÏòÅÎ¨∏Ïûê+ÌäπÏàòÎ¨∏Ïûê Ï°∞Ìï©ÏúºÎ°ú 8ÏûêÎ¶¨ Ïù¥ÏÉÅ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî :(
                </div>
              )}
              {/* {validatePwd && (
                <FontAwesomeIcon
                  icon={faCheck}
                  className={styles["check-icon"]}
                />
              )} */}
            </div>
          </div>

          <br />
          <div className={styles["input-group"]}>
            <label className={styles.label}>
              <h2 className={styles["input-label"]}>ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏</h2>
            </label>
            <div className={styles["input-field"]}>
              <input
                type="password"
                value={confirmPassword}
                onChange={pwdMatchCheckHandler}
                placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏"
                required
                className={styles["input"]}
              />
              {confirmPassword.length > 0 && isPwdMatched && (
                <div className={styles["input-field-valEmail"]}>
                  ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌï©ÎãàÎã§ :)
                </div>
              )}
              {confirmPassword.length > 0 && !isPwdMatched && (
                <div className={styles["input-field-inValEmail"]}>
                  ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§ :(
                </div>
              )}
              {/* {isPwdMatched && (
                <FontAwesomeIcon
                  icon={faCheck}
                  className={styles["check-icon"]}
                />
              )} */}
            </div>
          </div>

          <button
            className={`${styles["submit-button"]} ${(validateEmail && validatePwd && isPwdMatched) && styles["submit-button-able"]}`}
            type="submit"
            onClick={onNext}
            disabled={!validateEmail || !validatePwd || !isPwdMatched}
          >
            ÌôïÏù∏
          </button>
        </form>
      )}
      {count === 2 && (
        <SignupAgreement
          getAgreementValue={getAgreementValue}
          onNext={onNext}
        />
      )}
      {count === 3 && (
        <NickName
          getNickName={getNickName}
          onNext={onNext}
          setnickNameValue={setnickNameValue}
        />
      )}
      {count === 4 && <RegisterPhone allData={allData} />}
    </div>
  );
};

export default Signup1;
