import axios from "axios";
import React, { useState, useEffect } from "react";

const KaKaoCallback = () => {
    const [kakaoEmail, setKakaoEmail] = useState('');

    useEffect(() => {
        const params = new URL(document.location.toString()).searchParams;
        const code = params.get("code");
        const grantType = "authorization_code";
        const REST_API_KEY = `${process.env.REACT_APP_PUBLIC_REST_API_KEY}`;
        const REDIRECT_URI = `${process.env.REACT_APP_REDIRECT_KAKAO_URL}`;

        axios
            .post(
                `https://kauth.kakao.com/oauth/token?grant_type=${grantType}&client_id=${REST_API_KEY}&redirect_uri=${REDIRECT_URI}&code=${code}`,
                {},
                {
                    headers: {
                        "Content-type":
                            "application/x-www-form-urlencoded;charset=utf-8",
                    },
                }
            )
            .then(function (res) {
                console.log(res);
                const { data } = res;
                const { access_token } = data;
                axios
                    .post(
                        "https://kapi.kakao.com/v2/user/me",
                        {},
                        {
                            headers: {
                                Authorization: `Bearer ${access_token}`,
                                "Content-type":
                                    "application/x-www-form-urlencoded",
                            },
                        }
                    )
                    .then(function (res) {
                        console.log("Îç∞Ïù¥ÌÑ∞ ÏÑ±Í≥µ", res);
                        setKakaoEmail(res.data.kakao_account.email);

                        // fetch("/user/kakaologin", {
                        //     method: "post",
                        //     body: JSON.stringify({
                        //         email: res.data.kakao_account.email,
                        //         type: "k",
                        //     }),
                        // })
                        //     .then((response) => response.json())
                        //     .then((res) => console.log(res));
                    });
            })
            .catch(function (Error) {
                console.log("ERR", Error);
            });
    }, []);

    useEffect(() => {
        const fetchData = async () => {
          try {
            // ÎπÑÎèôÍ∏∞ ÏûëÏóÖ ÏàòÌñâ
            const response = await fetch(`/user/getUserInfo?email=${kakaoEmail}`, {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
              },
              body: JSON.stringify({ email: kakaoEmail }),
            });
      
            // Ï≤òÎ¶¨ ÏΩîÎìú
            if (response.ok) {
                const text = await response.text();
        
                if (text) {
                  try {
                    const data = JSON.parse(text);
                    console.log("ÏÇ¨Ïö© Î∂àÍ∞Ä üòä: " + data);
                  } catch (error) {
                    console.log("JSON.parse error: ", error);
                  } // JSON.parse try-catch
                } else {
                  console.log("response: Îπà ÏùëÎãµ");
                } // text
              } else {
                console.log("response!=200");
              } // response isn't ok
            } catch (err) {
              console.log("ÏÑúÎ≤Ñ ÌÜµÏã† ÏóêÎü¨ Î∞úÏÉù: " + err);
            }
        };
      
        fetchData();
      }, [kakaoEmail]);

    return <></>;
};

export default KaKaoCallback;
